def unit_of_work(metadata=None, timeout=None):
    """
    Returns a decorator with metadata and timeout attributes.
    """
    # Validate metadata parameter
    if metadata is not None and not isinstance(metadata, dict):
        raise TypeError("metadata must be a dictionary")

    # Validate timeout parameter
    if timeout is not None:
        if not isinstance(timeout, (int, float)):
            raise TypeError("timeout must be a number")
        if timeout < 0:
            raise ValueError("timeout cannot be negative")

    def decorator(func):
        def wrapper(*args, **kwargs):
            # Store the original function metadata
            func_name = getattr(func, "__name__", str(func))

            # Validate that first argument is a transaction if args exist
            if not args:
                raise ValueError(f"{func_name} must receive a transaction as first argument")

            tx = args[0]

            # Set transaction metadata if provided
            if metadata is not None:
                try:
                    tx.run("CALL dbms.setTXMetaData($metadata)", 
                          parameters={"metadata": metadata})
                except Exception as e:
                    raise RuntimeError(f"Failed to set transaction metadata: {e}")

            # Set transaction timeout if provided
            if timeout is not None:
                try:
                    # Convert timeout to milliseconds as Neo4j expects
                    timeout_ms = int(timeout * 1000)
                    tx.run("CALL dbms.setTransactionTimeout($timeout)", 
                          parameters={"timeout": timeout_ms})
                except Exception as e:
                    raise RuntimeError(f"Failed to set transaction timeout: {e}")

            # Execute the decorated function
            return func(*args, **kwargs)

        # Preserve the metadata attributes
        wrapper.metadata = metadata
        wrapper.timeout = timeout

        # Preserve the original function's metadata
        from functools import update_wrapper
        update_wrapper(wrapper, func)

        return wrapper

    return decorator

