def point_type(name, fields, srid_map):
    """Dynamically create a Point subclass.

    Args:
        name: Name of the new class
        fields: Field definitions for the point
        srid_map: Mapping of SRID identifiers
    Returns:
        A new Point subclass with defined fields and SRID handling
    """
    def __init__(self, *args, **kwargs):
        super(Point, self).__init__()
        for i, (field_name, _) in enumerate(fields.items()):
            if i < len(args):
                setattr(self, field_name, args[i])
            elif field_name in kwargs:
                setattr(self, field_name, kwargs[field_name])
            else:
                setattr(self, field_name, None)

    def __str__(self):
        field_values = [f"{name}={getattr(self, name)}" 
                       for name in fields.keys()]
        return f"{name}({', '.join(field_values)})"

    @property
    def srid(self):
        # Use srid_map to get the SRID value
        try:
            return srid_map.get(self.__class__.__name__, None)
        except AttributeError:
            return None

    # Create property getters/setters for each field
    field_properties = {}
    for field_name, field_type in fields.items():
        def make_getter(name):
            def getter(self):
                return getattr(self, f"_{name}", None)
            return property(getter)

        def make_setter(name):
            def setter(self, value):
                setattr(self, f"_{name}", value)
            return setter

        prop = make_getter(field_name)
        prop = prop.setter(make_setter(field_name))
        field_properties[field_name] = prop

    # Create the new class with all defined attributes
    cls_dict = {
        '__init__': __init__,
        '__str__': __str__,
        'srid': srid,
        **field_properties
    }

    return type(name, (Point,), cls_dict)

