    """
    Decorator to wrap a class or instance method with a memoizing
    callable that saves results in a cache.
    """
    def decorator(func):
        def wrapper(*args, **kwargs):
            # Generate cache key based on function args and kwargs
            cache_key = key(func, *args, **kwargs) if callable(key) else key

            if lock:
