    """Return attribute names and descriptions defined by interface."""
    result = {}

    # Get all properties and their docs
    properties = [(attr.__name__, attr.__doc__ or "No description available") 
                 for attr in self.__class__.__dict__.values() 
                 if isinstance(attr, property)]

    # Get interface methods and their docs
    methods = [(name, func.__doc__ or "No description available") 
              for name, func in self.__class__.__dict__.items()
              if callable(func) and not isinstance(func, property) 
              and not name.startswith('_')]

