        "type": "object",
        "properties": {
            "apiVersion": {"type": "string"},
            "kind": {"type": "string"},
            "metadata": {"$ref": "#/definitions/Metadata"},
            "spec": {"$ref": f"#/definitions/{resource_name}Spec"},
            "status": {"$ref": f"#/definitions/{resource_name}Status"}
        },
        "required": ["apiVersion", "kind", "metadata", "spec", "status"]
    }

def generate_default_observer_schema(app):
    """Generate the default observer schema for each Kubernetes resource.

    Args:
        app (krake.data.kubernetes.Application): The application for which to generate a
            default observer schema

    Returns:
        dict: Resource schemas mapped by resource name

    Raises:
        ValueError: If app or app.spec.manifest is None
    """
    if app is None or not hasattr(app, 'spec') or not hasattr(app.spec, 'manifest'):
        raise ValueError("Invalid application object provided")

    if not app.spec.manifest:
        return {}

    resource_schemas = {}

    try:
        for manifest in app.spec.manifest:
            # Skip if custom observer schema is already defined
            if manifest.observer_schema:
                continue

            if not hasattr(manifest, 'resource_name'):
                continue

            resource_name = manifest.resource_name

            # Get resource index if needed (prepared for future use)
            # resource_idx = get_kubernetes_resource_idx(resource_name)

            # Create schema for the resource
            resource_schema = _create_resource_schema(resource_name)

            # Store schema with deep copy to prevent reference issues
            resource_schemas[resource_name] = deepcopy(resource_schema)

    except Exception as e:
        raise ValueError(f"Error generating observer schema: {str(e)}")

    return resource_schemas

