def _group_files_by_xml_filename(xml_filename, files):
    """
    Group files by their XML basename and returns data in dict format.

    Parameters
    ----------
    xml_filename : str
        XML filenames
    files : list
        list of files in the folder or zipfile

    Returns
    -------
    dict
        key: name of the XML files
        value: Package
    """
    data = {}
    base_xml = os.path.splitext(xml_filename)[0]
    package = Package()

    # Group files that share the same basename as xml_filename
    for file in files:
        file_base = os.path.splitext(os.path.basename(file))[0]
        if file_base.startswith(base_xml):
            data.setdefault(xml_filename, package)
            # Add file to package based on extension
            if file.endswith('.xml'):
                package.add_asset(file)
            else:
                package.add_rendition(file)

    return data

