    """Call the given command(s).

    Args:
        commands (list): List of command names to try
        args (list): List of command arguments
        cwd (str, optional): Working directory for command execution
        verbose (bool, optional): Enable verbose output
        hide_stderr (bool, optional): Hide stderr output if True
        env (dict, optional): Environment variables for the subprocess

    Returns:
        tuple: (stdout output, return code) or (None, None) if command fails
    """
    # Input validation
    if not isinstance(commands, list):
        raise TypeError("commands must be a list")
    if not isinstance(args, list):
        raise TypeError("args must be a list")
    if cwd is not None and not isinstance(cwd, str):
        raise TypeError("cwd must be a string or None")
    if env is not None and not isinstance(env, dict):
        raise TypeError("env must be a dictionary or None")

    process = None
    popen_kwargs = {}

    if sys.platform == "win32":
        # This hides the console window if pythonw.exe is used
        startupinfo = subprocess.STARTUPINFO()
        startupinfo.dwFlags |= subprocess.STARTF_USESHOWWINDOW
        popen_kwargs["startupinfo"] = startupinfo

