        if verbose:
            print(f"Unable to find any valid command, tried: {commands}")
        return None, None

    try:
        stdout, stderr = process.communicate()
        stdout = stdout.strip().decode() if stdout else ""

        if verbose:
            print(f"Command output: {stdout}")
