def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__

    Args:
        bases: Tuple of base classes
        explicit_mc: Optional explicit metaclass specification

    Returns:
        The determined metaclass

    Raises:
        TypeError: If no valid metaclass can be determined or if bases are incompatible
    """
    # Handle case with explicit metaclass
    if explicit_mc is not None:
        if not isinstance(explicit_mc, type):
            raise TypeError("Explicit metaclass must be a type")
        return explicit_mc

    # Start with collecting metaclasses from bases
    metaclasses = []
    for base in bases:
        # Get metaclass of each base
        base_mc = type(base)
        if base_mc is not type:
            metaclasses.append(base_mc)

    # If no explicit metaclasses found, return type
    if not metaclasses:
        return type

    # If only one metaclass, return it
    if len(metaclasses) == 1:
        return metaclasses[0]

    # Multiple metaclasses - find most derived metaclass
    candidate = metaclasses[0]
    for mc in metaclasses[1:]:
        if issubclass(candidate, mc):
            continue
        if issubclass(mc, candidate):
            candidate = mc
        else:
            raise TypeError(
                "Incompatible metaclasses found: {0} and {1}".format(
                    candidate.__name__, mc.__name__
                )
            )

    return candidate

