def identify_request(request: RequestType) -> bool:
    """
    Identify if the request is a Diaspora request by checking:
    1. Public message format
    2. Private message format
    3. Legacy payload format

    Args:
        request: RequestType object containing either JSON or XML data

    Returns:
        bool: True if request is identified as Diaspora request, False otherwise
    """
    try:
        if isinstance(request, JSON):
            # Decode JSON data if needed
            data = decode_if_bytes(request.data)
            if isinstance(data, str):
                data = json.loads(data)

            # Check for public message format
            if "events" in data and isinstance(data["events"], list):
                return True

            # Check for private message format
            if "aes_key" in data and "encrypted_magic_envelope" in data:
                return True

            # Check for legacy payload
            if "diaspora" in data and "source" in data:
                return True

        elif isinstance(request, XML):
            # Decode XML data if needed
            data = decode_if_bytes(request.data)
            if isinstance(data, str):
                xml_root = fromstring(data)

                # Check for Magic Envelope format
                if xml_root.tag == "Magic_ENV_TAG":
                    return True

                # Check for legacy XML format
                if xml_root.tag == "XML" and xml_root.find(".//diaspora") is not None:
