    Controls arguments: control the IR behavior. These arguments
        will not be put into the spec yml file
    Nested arguments: are used by the Ansible playbooks and will be put
        into the spec yml file.
    Custom arguments: Custom ansible variables to be used instead of the
        normal nested usage.

    :param args: the collected list of args.
    :return: (dict, dict): flat dicts (control_args, nested_args)
    """
    control_args = {}
    nested_args = {}

    if not isinstance(args, dict):
        LOG.warning("Input args must be a dictionary")
        return control_args, nested_args

    try:
        for arg_name, arg_value in args.items():
            # Validate argument name is string
            if not isinstance(arg_name, Str):
                LOG.warning(f"Argument name '{arg_name}' must be a string")
                continue

            # Handle control arguments (starting with -- or -)
            if arg_name.startswith('--'):
                control_args[arg_name[2:]] = arg_value
            elif arg_name.startswith('-'):
                control_args[arg_name[1:]] = arg_value
            else:
                # Handle nested and custom arguments
                if isinstance(arg_value, tuple(COMPLEX_TYPES)):
                    # Complex types go to nested args
                    nested_args[arg_name] = arg_value
                elif arg_name in arg_spec.get('custom_vars', []):
                    # Known custom variables
                    nested_args[f"custom_{arg_name}"] = arg_value
                else:
                    # Regular nested arguments
                    nested_args[arg_name] = arg_value

            # Validate argument values
            if arg_value is None:
                LOG.warning(f"Argument '{arg_name}' has None value")

    except Exception as e:
        LOG.warning(f"Error processing arguments: {format(e)}")
        return {}, {}

    return control_args, nested_args

