def merge_extra_vars(vars_dict, extra_vars=None):
    """
    Extend ``vars_dict`` with ``extra-vars``

    :param vars_dict: Dictionary to merge extra-vars into
    :param extra_vars: List of extra-vars
    :raises IRExtraVarsException: If input types are invalid or merge fails
    """
    # Validate vars_dict is a dictionary
    if not isinstance(vars_dict, dict):
        raise IRExtraVarsException("vars_dict must be a dictionary")

    # Handle default case
    if extra_vars is None:
        return

    # Ensure extra_vars is a list
    if not isinstance(extra_vars, list):
        raise IRExtraVarsException("extra_vars must be a list")

    # Process and merge extra vars
    try:
        for item in extra_vars:
            # Handle dictionary items
            if isinstance(item, dict):
                vars_dict.update(item)
            # Handle key-value tuple pairs
            elif isinstance(item, tuple) and len(item) == 2:
                key, value = item
                vars_dict[key] = value
            # Handle string key=value pairs
            elif isinstance(item, str) and '=' in item:
                key, value = item.split('=', 1)
                vars_dict[key.strip()] = value.strip()
            else:
                raise IRExtraVarsException(
                    f"Invalid extra_vars item format: {item}. "
                    "Must be dict, tuple pair, or 'key=value' string"
                )
    except Exception as e:
        raise IRExtraVarsException(f"Failed to merge extra vars: {str(e)}")

