import os
import yaml
import logging
from typing import Dict

def get_plugin_spec_flatten_dict(plugin_dir: str) -> Dict:
    """
    Creates a flat dict from the plugin spec

    :param plugin_dir: A path to the plugin's dir
    :return: A flatten dictionary contains the plugin's properties
    :raises: FileNotFoundError if plugin_dir doesn't exist
    """
    if not os.path.exists(plugin_dir):
        raise FileNotFoundError(f"Plugin directory '{plugin_dir}' does not exist")

    plugin_spec = {}

    try:
        for root, dirs, files in os.walk(plugin_dir):
            for file in files:
                if file.endswith(".yaml"):
                    file_path = os.path.join(root, file)
                    try:
                        with open(file_path, "r", encoding='utf-8') as f:
                            try:
                                data = yaml.safe_load(f)
                                if data:  # Check if data is not None
                                    # Log if we're overwriting existing keys
                                    overlapping_keys = set(plugin_spec.keys()) & set(data.keys())
                                    if overlapping_keys:
                                        logging.warning(
                                            f"Overwriting existing keys {overlapping_keys} "
                                            f"from file {file_path}"
                                        )
                                    plugin_spec.update(data)
                            except yaml.YAMLError as yaml_err:
                                logging.error(f"Error parsing YAML file {file_path}: {yaml_err}")
                    except IOError as io_err:
                        logging.error(f"Error reading file {file_path}: {io_err}")
                        continue
    except Exception as e:
        logging.error(f"Unexpected error while processing plugin directory: {e}")
        raise

    return plugin_spec

