def point_type(name: str, fields: list[str], srid_map: dict) -> type:
    """
    Dynamically create a Point subclass.

    Args:
        name: Name of the point type class
        fields: List of field names for coordinates
        srid_map: Dictionary mapping SRID values to their definitions

    Returns:
        A Point subclass with the specified fields
    """

    class DynamicPoint(Point):
        def __init__(self, *args, **kwargs):
            if len(args) > len(fields):
                raise ValueError(f"Expected {len(fields)} positional arguments, got {len(args)}")

            # Initialize with default None values
            for field in fields:
                setattr(self, field, None)

            # Set positional arguments
            for field, value in zip(fields, args):
                setattr(self, field, value)

            # Set keyword arguments
            for field, value in kwargs.items():
                if field not in fields:
                    raise AttributeError(f"Unknown field: {field}")
                setattr(self, field, value)

            # Validate all required fields are set
            for field in fields:
                if getattr(self, field) is None:
                    raise ValueError(f"Missing required field: {field}")

        def __repr__(self):
            field_values = []
            for field in fields:
                value = getattr(self, field)
                field_values.append(f"{field}={value!r}")
            return f"{name}({', '.join(field_values)})"

        def to_geojson(self, srid: int) -> dict:
            """Convert point to GeoJSON format for the given SRID"""
            if not isinstance(srid, int):
                raise TypeError("SRID must be an integer")

            if srid not in srid_map:
                raise ValueError(f"Invalid SRID: {srid}")

            coordinates = [getattr(self, field) for field in fields]

            return {
                "type": "Point",
                "coordinates": coordinates
            }

    # Set the dynamic class name
    DynamicPoint.__name__ = name

    return DynamicPoint

