import subprocess
import sys

def run_command(commands, args, cwd=None, verbose=False, hide_stderr=False, env=None):
    """Call the given command(s)."""
    if env is None:
        env = {}

    # Handle both string and list inputs
    if isinstance(commands, str):
        commands = [commands]
    if isinstance(args, str):
        args = [args]

    # Create full command list
    full_command = commands + (args if args else [])

    if verbose:
        print(f"Running command: {' '.join(full_command)}")

    try:
        # Set up Windows-specific startup info if needed
        startupinfo = None
        if sys.platform == 'win32':
            startupinfo = subprocess.STARTUPINFO()
            startupinfo.dwFlags |= subprocess.STARTF_USESHOWWINDOW

        # Use list form of command to avoid shell injection risks
        process = subprocess.Popen(
            full_command,
            cwd=cwd,
            shell=False,  # Safer than shell=True
            stdout=subprocess.PIPE,
            stderr=subprocess.PIPE,
            env=env,
            startupinfo=startupinfo,
            universal_newlines=True  # Handle text encoding
        )

        output, error = process.communicate()

        if not hide_stderr and error:
            print(error.strip())

        if process.returncode != 0:
            print(f"Command failed with return code {process.returncode}")
            return None

        return output.strip()

    except Exception as e:
        print(f"Error running command: {' '.join(full_command)}")
        print(str(e))
        return None

