        params.update({'include_fields': params.get('include_fields', []) + ['security']})

    # Add assignee check
    if self.has_assignee():
        params.update({'include_fields': params.get('include_fields', []) + ['assigned_to']})

    # Add needinfo check
    if self.has_needinfo():
        params.update({'include_fields': params.get('include_fields', []) + ['flags']})

    # Filter bugs with no nag keyword
    params = self.filter_no_nag_keyword(params)

