def deep_merge_nodes(nodes):
    """Deep merge YAML nodes with duplicate keys, later values taking precedence."""
    if not nodes:
        return []

    # Convert nodes list to a mapping of key values to their corresponding values
    merged = {}
    for node_tuple in nodes:
        if not isinstance(node_tuple, tuple):
            continue

        key_node, value_node = node_tuple
        key = key_node.value if hasattr(key_node, 'value') else str(key_node)

        # Handle collision cases
        if key in merged:
            existing_value = merged[key]
            # If both nodes are mapping nodes, merge them recursively
            if (isinstance(existing_value, MappingNode) and 
                isinstance(value_node, MappingNode)):
                merged[key] = MappingNode(
                    tag='tag:yaml.org,2002:map',
                    value=deep_merge_nodes(existing_value.value + value_node.value)
                )
            else:
                # For non-mapping nodes, last value wins
                merged[key] = value_node
        else:
            merged[key] = value_node

    # Convert back to list of tuples format
    result = []
    for key, value in merged.items():
        key_node = next((node[0] for node in nodes if node[0].value == key), None)
        if key_node:
            result.append((key_node, value))

    return result

