    suffixes = {
        'B': 1,
        'K': 1000,
        'M': 1000 ** 2,
        'G': 1000 ** 3,
        'T': 1000 ** 4
    }

    # Handle bare numbers without suffix
    if size.isdigit():
        return int(size)

    # Extract numeric part and suffix
