    # Remove 's' from the end if present (handle both singular and plural)
    if timeunit.endswith('s'):
        timeunit = timeunit[:-1]

    # Map of time units to their timedelta parameter names
    time_units = {
        'day': 'days',
        'hour': 'hours',
        'minute': 'minutes',
        'second': 'seconds',
        'microsecond': 'microseconds',
        'week': 'weeks'
    }

    if timeunit not in time_units:
        raise ValueError("Invalid time unit")

    # Create the timedelta with the appropriate unit
    return datetime.timedelta(**{time_units[timeunit]: number})

