import rdflib

def find_roots(
    graph: rdflib.Graph, prop: rdflib.URIRef, roots: Optional[Set[rdflib.Node]] = None
) -> Set[rdflib.Node]:
    # Initialize roots set if not provided
    if roots is None:
        roots = set()
        # Add all subjects that appear as children
        for child, _, _ in graph.triples((None, prop, None)):
            roots.add(child)

    # Find all nodes that have parents (non-roots)
    non_roots = set()
    for _, _, parent in graph.triples((None, prop, None)):
        non_roots.add(parent)
        # Remove any node that has a parent from roots
        if parent in roots:
            roots.remove(parent)

    return roots

