def identify_request(request: RequestType) -> bool:
    """
    Identify whether this is a Diaspora request.
    Checks for public message, private message, and legacy payload.

    Args:
        request: RequestType object containing either JSON or XML data

    Returns:
        bool: True if it's a valid Diaspora request, False otherwise
    """
    try:
        if isinstance(request, JSON):
            # Decode if bytes
            if hasattr(request, 'decode'):
                request = decode_if_bytes(request)

            # Parse JSON
            data = loads(request)

            # Check for public message
            if 'events' in data:
                return True
            # Check for private message
            if 'aes_key' in data or 'encrypted_magic_envelope' in data:
                return True

        elif isinstance(request, XML):
            # Parse XML if string
            if isinstance(request, str):
                request = fromstring(encode_if_text(request))

            # Check for magic envelope (legacy payload)
            if request.tag == 'Magic_ENV_TAG':
                return True
            # Check for alternative XML formats
            if request.find('.//post') is not None:
                return True

    except (ValueError, AttributeError, ElementTree.ParseError):
        return False

    return False

