@classmethod
def validate_from_content(cls, spec_content=None):
    """
    Validates that spec (YAML) content has all required fields

    :param spec_content: content of spec file
    :raise IRValidatorException: when mandatory data is missing in spec file 
    :return: Dictionary with data loaded from a spec (YAML) file
    """
    if spec_content is None:
        spec_content = ""

    try:
        spec_data = yaml.safe_load(spec_content)
    except yaml.YAMLError:
        raise IRValidatorException("Invalid spec content")

    if spec_data is None:
        raise IRValidatorException("Empty spec content")

    if not isinstance(spec_data, dict):
        raise IRValidatorException("Spec content must be a YAML dictionary")

    required_fields = ["field1", "field2", "field3"]

    for field in required_fields:
        if field not in spec_data:
            raise IRValidatorException(f"Missing required field: {field}")

    return spec_data

@classmethod
def validate_from_file(cls, yaml_file=None):
    """Loads & validates that a YAML file has all required fields

    :param yaml_file: Path to YAML file
    :raise IRValidatorException: when mandatory data is missing in file
    :return: Dictionary with data loaded from a YAML file
    """
    if yaml_file is None or not isinstance(yaml_file, str):
        raise IRValidatorException(
            "YAML file path must be a valid string")

    if not os.path.isfile(yaml_file):
        raise IRValidatorException(
            "The specified YAML file does not exist: {}".format(yaml_file))

    try:
        with open(yaml_file) as fp:
            spec_dict = cls.validate_from_content(fp.read())
    except (IOError, OSError) as e:
        raise IRValidatorException(
            "Error reading YAML file {}: {}".format(yaml_file, str(e)))

    return spec_dict

def _include_groups(self, parser_dict):
    """Resolves the include dict directive in the spec files."""
    for group in parser_dict.get('include_groups', []):
        grp_dict = next((grp for grp in self.spec_dict.get('shared_groups', 
            []) if grp['title'] == group), None)
        if grp_dict is None:
            raise SpecParserException(
                "Unable to include group '{}' in '{}' parser. Group was not found!"
                .format(group, parser_dict['name']))

        # Create a deep copy to avoid modifying the original
        grp_dict_copy = deepcopy(grp_dict)

        # Mark options as shared group options
        if 'options' in grp_dict_copy:
            for option in grp_dict_copy['options'].values():
                option['is_shared_group_option'] = True

        # Initialize groups list if not present
        if 'groups' not in parser_dict:
            parser_dict['groups'] = []

        parser_dict['groups'].append(grp_dict_copy)

    return parser_dict

def get_spec_defaults(self):
    """Resolve arguments' values from spec and other sources."""
    defaults = {}

    try:
        # Get default values from spec parser
        spec_defaults = self._get_defaults()

        for option in spec_defaults:
            # Check environment variables first
            env_value = self.get_env_option(option)
            if env_value:
                defaults[option] = env_value
            else:
                # Use spec default if no environment override
                defaults[option] = spec_defaults.get(option)

        LOG.info("Resolved spec defaults: {}".format(defaults))
        return defaults

    except Exception as e:
        LOG.info("Error resolving spec defaults: {}".format(str(e)))
        return {}

