def validate_min_max_args(self, args):
    """
    Check if value of arguments is between minimum and maximum values.

    :param args: The received arguments.
    :return: True if all arguments are within range, False otherwise
    :raises IRInvalidMinMaxRangeException: If args is None or of invalid type
    """
    if args is None:
        raise IRInvalidMinMaxRangeException("Arguments cannot be None")

    # Handle both single value and iterable cases
    if isinstance(args, (int, float)):
        values = [args]
    elif hasattr(args, '__iter__'):
        values = args
    else:
        raise IRInvalidMinMaxRangeException(f"Invalid argument type: {type(args)}")

    try:
        # Check if all values are within range
        return all(self.minimum <= value <= self.maximum for value in values)
    except TypeError:
        raise IRInvalidMinMaxRangeException("Invalid value type in arguments")

