import os
import yaml

def get_plugin_spec_flatten_dict(plugin_dir):
    """
    Creates a flat dict from the plugin spec

    :param plugin_dir: A path to the plugin's dir
    :return: A flatten dictionary contains the plugin's properties
    """
    def flatten_dict(d, parent_key='', sep='.'):
        items = []
        for k, v in d.items():
            new_key = f"{parent_key}{sep}{k}" if parent_key else k
            if isinstance(v, dict):
                items.extend(flatten_dict(v, new_key, sep=sep).items())
            else:
                items.append((new_key, v))
        return dict(items)

    plugin_spec = {}

    # Walk through the directory
    for root, dirs, files in os.walk(plugin_dir):
        for file in files:
            if file.endswith(".yaml"):
                file_path = os.path.join(root, file)
                try:
                    with open(file_path, "r") as f:
                        data = yaml.safe_load(f)
                        if isinstance(data, dict):
                            # Flatten the dictionary and update plugin_spec
                            flattened_data = flatten_dict(data)
                            plugin_spec.update(flattened_data)
                except (yaml.YAMLError, IOError) as e:
                    continue  # Skip files with YAML parsing errors or IO errors

    return plugin_spec

def strip_root(path: str, root: str) -> str:
    if not path.startswith(root):
        raise Exception(f"Path '{path}' does not start with root '{root}'")
    return path[len(root):]
