def point_type(name, fields, srid_map):
    """Dynamically create a Point subclass."""

    attributes = {}

    # Add field properties to the class
    for i, field in enumerate(fields):
        def make_getter(idx=i):
            def getter(self):
                return self[idx]
            return property(getter)

        attributes[field] = make_getter()

    # Add SRID mapping functionality
    def get_srid(self):
        if len(self) > 0 and hasattr(self, 'srid'):
            return self.srid
        return srid_map.get(name)

    attributes['get_srid'] = get_srid

    # Create and return the subclass of Point
    return type(name, (Point,), attributes)

