    if not filename.startswith(prefix) or filename.endswith(".xml"):
        return None

    # Get file extension
    _, extension = os.path.splitext(filename)
    extension = extension.lower()

    # Create base dictionary with common attributes
    result = {"component_id": filename, "file_path": file_path}

    # Determine file type (asset or rendition)
