    if not issue:
        return None, None

    issue = issue.strip().replace('.', '')
    splitted = [s for s in issue.split() if s]

    # Handle empty list case
    if not splitted:
        return None, None

    # Normalize 'spe' and 'supl'/'suppl' variants
    normalized = []
    for s in splitted:
        if 'spe' in s.lower() and s.lower() != 'spe':
            if s.lower().startswith('spe') and s[3:].isdigit():
                normalized.append('spe')
                normalized.append(s[3:])
            else:
                normalized.append('spe')
        elif 'sup' in s.lower() and s.lower() not in ['suppl', 'supp', 'supl']:
            if s.lower().startswith('suppl') and s[5:].isdigit():
                normalized.append('suppl')
                normalized.append(s[5:])
            elif s.lower().startswith('supp') and s[4:].isdigit():
                normalized.append('suppl')
                normalized.append(s[4:])
            else:
                normalized.append('suppl')
        else:
            normalized.append(s)

    splitted = normalized

    # Process based on number of elements
    if len(splitted) == 1:
        issue = splitted[0]
        if issue.isdigit():
            return issue, None
        if 'sup' in issue.lower():
            return None, '0'
        if issue.lower() == 'spe':
            return None, '0'
        if issue.startswith('s') and issue[1:].isdigit():
            return None, issue[1:]
        return issue, None

    elif len(splitted) == 2:
        if 'sup' in splitted[0].lower() or splitted[0].lower() == 'spe':
            return None, splitted[1] if splitted[1].isdigit() else '0'
        if 'sup' in splitted[1].lower() or splitted[1].lower() == 'spe':
            return splitted[0], '0'
        if splitted[0].isdigit() and splitted[1].isdigit():
            return splitted[0], splitted[1]
        return splitted[0], None

    elif len(splitted) == 3:
        if ('sup' in splitted[1].lower() or splitted[1].lower() == 'spe') and splitted[0].isdigit():
            return splitted[0], splitted[2] if splitted[2].isdigit() else '0'
        if ('sup' in splitted[0].lower() or splitted[0].lower() == 'spe') and splitted[1].isdigit():
            return None, splitted[1]

    # For any other cases, join everything as the issue number
    return ''.join(splitted), None

