def parse_frequency(frequency):
    if frequency is None or frequency == "always":
        return None

    parts = frequency.split()
    if len(parts) != 2:
        raise ValueError("Invalid frequency format")

    number = int(parts[0])
    timeunit = parts[1].lower().strip()

    # Handle singular/plural forms by removing trailing 's'
    if timeunit.endswith('s') and timeunit != 'microseconds' and timeunit != 'milliseconds':
        timeunit = timeunit[:-1]

    # Map timeunit to the correct parameter for timedelta
    if timeunit in ['day', 'days']:
        return datetime.timedelta(days=number)
    elif timeunit in ['hour', 'hours']:
        return datetime.timedelta(hours=number)
    elif timeunit in ['minute', 'minutes']:
        return datetime.timedelta(minutes=number)
    elif timeunit in ['second', 'seconds']:
        return datetime.timedelta(seconds=number)
    elif timeunit in ['microsecond', 'microseconds']:
        return datetime.timedelta(microseconds=number)
    elif timeunit in ['millisecond', 'milliseconds']:
