    try:
        data = json.loads(decode_if_bytes(request.body))
        if 'encrypted_magic_envelope' in data:
            return 2  # Private message
    except Exception:
        pass

    # Check for public message (XML)
    try:
        xml = etree.fromstring(encode_if_text(request.body))
        if xml.tag == MAGIC_ENV_TAG:
            return 1  # Public message
    except Exception:
        pass

    # If neither private nor public, it's a legacy payload
    return 0  # Legacy payload

