def _include_groups(self, parser_dict):
    """
    Resolves the include dict directive in the spec files.
    """
    # Check if there's an 'include' directive in the parser_dict
    if 'include' not in parser_dict:
        return parser_dict

    result = deepcopy(parser_dict)
    includes = result.pop('include')

    # Process each include directive
    for include in includes:
        if not isinstance(include, Str):
            raise SpecParserException("Include directive must be a string")

        # Get the included parser dictionary
        try:
            included_dict = self.get(include)
            if not included_dict:
                raise SpecParserException("Could not find include: {}".format(include))

            # Merge the included dictionary with the result
            for key, value in included_dict.items():
                if key in result:
                    # If the key already exists and is a list, append values
                    if isinstance(result[key], list) and isinstance(value, list):
                        result[key].append(value)
                    # Otherwise, replace the value
                    else:
                        result[key] = deepcopy(value)
                else:
                    # Add the new key-value pair
                    result[key] = deepcopy(value)
        except Exception as e:
            raise SpecParserException("Error processing include '{}': {}".format(include, str(e)))

    return result

