def retrieve_and_parse_diaspora_webfinger(handle):
    # Extract the domain from the handle to use in the URL
    if '@' in handle:
        username, domain = handle.split('@', 1)
    else:
        # If no @ in handle, it might already be a domain or improperly formatted
        domain = handle

    # Construct the proper webfinger URL with the correct domain
    url = f"https://{domain}/.well-known/webfinger?resource=acct:{handle}"

    try:
        # Make the request with proper error handling
        response = requests.get(url, timeout=10)
        response.raise_for_status()  # Raise exception for 4XX/5XX responses

        # Check content type - WebFinger uses JSON, not XML
        if "application/json" in response.headers.get('Content-Type', ''):
            webfinger_data = response.json()
            result = {}

            # Extract data from JSON format
            for link in webfinger_data.get('links', []):
                rel = link.get('rel')
                if rel == "http://webfinger.net/rel/profile-page":
                    result["profile_page"] = link.get('href')
                elif rel == "http://schemas.google.com/g/2010#updates-from":
                    result["updates_from"] = link.get('href')

            # Add alias if available
            if 'aliases' in webfinger_data and webfinger_data['aliases']:
                result["alias"] = webfinger_data['aliases'][0]

            return result
        else:
            # Handle XML response (older WebFinger implementations)
            xml_data = response.content
            root = ET.fromstring(xml_data)

            webfinger_data = {}

            # Parse XML namespace correctly
            ns = {'xrd': 'http://docs.oasis-open.org/ns/xri/xrd-1.0'}

            # Find aliases
            for alias in root.findall('.//xrd:Alias', ns):
                webfinger_data["alias"] = alias.text
                break  # Take the first alias

            # Find links
            for link in root.findall('.//xrd:Link', ns):
                rel = link.get("rel")
                if rel == "http://webfinger.net/rel/profile-page":
                    webfinger_data["profile_page"] = link.get("href")
                elif rel == "http://schemas.google.com/g/2010#updates-from":
                    webfinger_data["updates_from"] = link.get("href")

            return webfinger_data

    except requests.exceptions.RequestException:
        # Handle request exceptions gracefully
        return {}

