def _legacy_mergeOrderings(orderings: List[List[Any]]) -> List[Any]:
    if not orderings:
        return []

    # Create a mapping of elements to their positions in each ordering
    element_to_orderings = {}
    for i, ordering in enumerate(orderings):
        for j, element in enumerate(ordering):
            if element not in element_to_orderings:
                element_to_orderings[element] = {}
            element_to_orderings[element][i] = j

    # Identify elements that can start the merged ordering
    # (elements that are at the beginning of at least one ordering)
    potential_starts = set()
    for i, ordering in enumerate(orderings):
        if ordering:  # Skip empty orderings
            potential_starts.add(ordering[0])

    # Merge the orderings
    merged_ordering = []
    used_elements = set()

    while potential_starts:
        # Choose a start element that hasn't been used yet
        start_element = None
        for element in potential_starts:
            if element not in used_elements:
                start_element = element
                break

        if start_element is None:
            break

        # Process the element and find the next potential starts
        merged_ordering.append(start_element)
        used_elements.add(start_element)

        next_potential_starts = set()
        for i, ordering in enumerate(orderings):
            if start_element in ordering:
                idx = ordering.index(start_element)
                if idx + 1 < len(ordering):
                    next_element = ordering[idx + 1]
                    next_potential_starts.add(next_element)

        potential_starts = next_potential_starts

    # Add any remaining elements from orderings that weren't processed
    for ordering in orderings:
        for element in ordering:
            if element not in used_elements:
                merged_ordering.append(element)
                used_elements.add(element)

    return merged_ordering

