def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    metaclass = None

    # If there's an explicit metaclass, start with it
    if explicit_mc is not None:
        metaclass = explicit_mc

    # Get unique base metaclasses (remove redundant bases)
    minimal_bases = minimalBases(bases)

    # Collect metaclasses from all bases
    metaclasses = []
    for base in minimal_bases:
        # Get metaclass from base
        base_metaclass = getattr(base, '__class__', type(base))

        # Skip if it's the standard ClassType
        if base_metaclass is ClassType:
            continue

        # Add to our list of metaclasses
        if base_metaclass not in metaclasses:
            metaclasses.append(base_metaclass)

    # If we found base metaclasses
    if metaclasses:
        if metaclass is None:
            # Use the first one if no explicit metaclass
            metaclass = metaclasses[0]

        # Check compatibility with all base metaclasses
        for meta in metaclasses:
            if not issubclass(metaclass, meta):
                raise TypeError(
                    "Incompatible metaclasses: %r and %r" % (metaclass, meta)
                )

    # Default to type if no metaclass found
    if metaclass is None:
        metaclass = type

    return metaclass

