def unit_of_work(metadata=None, timeout=None):
    def decorator(func):
        def wrapper(*args, **kwargs):
            # Validate parameters
            if metadata is not None and not isinstance(metadata, dict):
                raise TypeError("metadata must be a dictionary")

            if timeout is not None:
                if not isinstance(timeout, (int, float)):
                    raise TypeError("timeout must be a number")
                if timeout < 0:
                    raise ValueError("timeout cannot be negative")

            # Get the transaction object from the first argument
            if not args:
                raise ValueError("Transaction object must be provided as first argument")
            tx = args[0]

            # Set transaction metadata if provided
            if metadata:
                tx.run("CALL dbms.setTXMetaData($metadata)", 
                      metadata=metadata)

            # Set transaction timeout if provided
            if timeout is not None:
                tx.run("CALL dbms.setTransactionTimeout($timeout)", 
                      timeout=int(timeout * 1000))  # Convert to milliseconds

            # Execute the original function
            return func(*args, **kwargs)

        # Preserve the original function's metadata
        wrapper.__name__ = func.__name__
        wrapper.__doc__ = func.__doc__
        return wrapper
    return decorator

