def point_type(name, fields, srid_map):
    """Dynamically create a Point subclass.

    Args:
        name: Name of the new Point subclass
        fields: Field definitions for the point type
        srid_map: Mapping of SRID values

    Returns:
        A new Point subclass with the specified fields and SRID mapping
    """
    # Create class attributes dictionary
    attrs = {
        'fields': property(lambda self: fields),
        'srid_map': property(lambda self: srid_map),
    }

    # Add field properties
    for i, field in enumerate(fields):
        def make_getter(index):
            def getter(self):
                return self[index]
            return property(getter)
        attrs[field] = make_getter(i)

    # Create new Point subclass
    return type(name, (Point,), attrs)

