from functools import wraps

def tzname_in_python2(namefunc):
    """
    Decorator to change unicode output into bytestrings in Python 2.
    tzname() API changed in Python 3 - used to return bytes, now returns unicode strings.
    """
    @wraps(namefunc)
    def wrapper(self, dt):
        name = namefunc(self, dt)
        if name is not None:
            return name.encode('utf-8')
        return name
    return wrapper

