import argparse

def parse_arguments(*arguments):
    """Parse command-line arguments and return an ArgumentParser instance.

    Args:
        *arguments: Variable length argument list to be added to parser

    Returns:
        ArgumentParser: Configured argument parser instance with parsed arguments
    """
    parser = argparse.ArgumentParser()

    # Only add arguments if they are provided
    if arguments:
        for arg in arguments:
            if isinstance(arg, str):
                parser.add_argument(arg)
            elif isinstance(arg, (list, tuple)):
                parser.add_argument(*arg)
            else:
                raise TypeError(f"Unsupported argument type: {type(arg)}")

    # Parse the arguments
    return parser.parse_args()

