def determineMetaclass(bases, explicit_mc=None):
    """Determine metaclass from 1+ bases and optional explicit __metaclass__"""

    # If explicit metaclass is provided, use it
    if explicit_mc is not None:
        return explicit_mc

    # If no bases, default to type
    if not bases:
        return type

    # Get metaclasses from all bases
    metaclasses = []
    for base in bases:
        # Get metaclass either from __metaclass__ attribute or type(base)
        metaclass = getattr(base, '__metaclass__', type(base))
        if metaclass not in metaclasses:
            metaclasses.append(metaclass)

    # If only one metaclass, return it
    if len(metaclasses) == 1:
        return metaclasses[0]

    # If multiple metaclasses, find the most derived compatible metaclass
    candidate = metaclasses[0]
    for metaclass in metaclasses[1:]:
        if issubclass(candidate, metaclass):
            continue
        if issubclass(metaclass, candidate):
            candidate = metaclass
            continue
        # If metaclasses are not compatible, raise TypeError
        raise TypeError("Incompatible metaclasses")

