    """Parse an ISO-8601 datetime string into a datetime.datetime object."""
    import datetime
    from datetime import timedelta

    if not isinstance(dt_str, str):
        raise ValueError("Input must be a string")

    # Split date and time parts
    parts = dt_str.replace(',', '.').upper().split('T', 1)
    if len(parts) > 2:
        raise ValueError("Too many T separators")

    date_str = parts[0]
    time_str = parts[1] if len(parts) > 1 else None

    # Parse date part
    date_part = _parse_isodate(date_str)

    # If no time part, return date with minimum time
    if not time_str:
        return datetime.datetime(date_part.year, date_part.month, date_part.day)

    # Parse time part
    hour, minute, second, microsecond, tzinfo = _parse_isotime(time_str)

    # Handle special case of midnight represented as 24:00
    if hour == 24:
        if minute != 0 or second != 0 or microsecond != 0:
            raise ValueError("Hour 24 must be followed by 00:00:00")
        hour = 0
        date_part = date_part + timedelta(days=1)

    try:
        return datetime.datetime(
            date_part.year, date_part.month, date_part.day,
            hour, minute, second, microsecond, tzinfo=tzinfo
        )
    except ValueError as e:
        raise ValueError(f"Invalid datetime values: {e}")

