from typing import Dict
import json
import warnings

def parse_diaspora_webfinger(document: str) -> Dict:
    """Parse Diaspora webfinger document which can be in JSON or XRD format.

    Args:
        document: String containing WebFinger document content

    Returns:
        Dict containing parsed data with keys:
        - hcard_url: URL to the person's hCard
        - seed_url: URL to the person's pod server
    """
    try:
        # Try parsing as JSON first (new format)
        data = json.loads(document)

        result = {}
        if "links" not in data:
            return result

        for link in data["links"]:
            # Extract hCard URL
            if link.get("rel") == "http://microformats.org/profile/hcard":
                result["hcard_url"] = link.get("href")

            # Extract seed location URL    
            if link.get("rel") == "http://joindiaspora.com/seed_location":
                result["seed_url"] = link.get("href")

        return result

    except json.JSONDecodeError:
        # If JSON parsing fails, try parsing as XRD (old format)
        warnings.warn("Document appears to be XRD format, which is deprecated")

        result = {}

        # Basic XRD parsing - look for Link elements
        if "Link" in document:
            # Extract hCard URL
            if 'rel="http://microformats.org/profile/hcard"' in document:
                # Very basic extraction - in practice should use proper XML parsing
                start = document.find('href="', document.find('rel="http://microformats.org/profile/hcard"'))
                if start != -1:
                    end = document.find('"', start + 6)
                    if end != -1:
                        result["hcard_url"] = document[start + 6:end]

            # Extract seed location
            if 'rel="http://joindiaspora.com/seed_location"' in document:
                start = document.find('href="', document.find('rel="http://joindiaspora.com/seed_location"'))
                if start != -1:
                    end = document.find('"', start + 6)
                    if end != -1:
                        result["seed_url"] = document[start + 6:end]

        return result

