import requests
import json

def retrieve_and_parse_diaspora_webfinger(handle):
    """
    Retrieve and parse a remote Diaspora webfinger document.

    :arg handle: Remote handle to retrieve
    :returns: dict containing webfinger data
    """
    # Parse the handle to get the domain
    try:
        username, domain = handle.split("@")
    except ValueError:
        return {}

    # Construct proper webfinger URL
    url = f"https://{domain}/.well-known/webfinger"
    params = {
        "resource": f"acct:{handle}"
    }

    webfinger = {}
    try:
        response = requests.get(url, params=params, timeout=10)

        if response.status_code == 200:
            try:
                data = response.json()
                # Extract links from JSON response
                if "links" in data:
                    for link in data["links"]:
                        rel = link.get("rel")
                        href = link.get("href")
                        if rel and href:
                            webfinger[rel] = href
            except json.JSONDecodeError:
                pass

    except (requests.exceptions.RequestException, ValueError):
        pass

    return webfinger

