def _include_groups(self, parser_dict):
    """
    Resolves the include dict directive in the spec files.

    Args:
        parser_dict (dict): Dictionary containing parser configuration

    Returns:
        dict: Updated parser dictionary with included groups resolved
    """
    from copy import deepcopy

    if not isinstance(parser_dict, dict):
        raise SpecParserException("Parser dictionary must be a dict type")

    # Create a copy of the original dict to avoid modifying it directly
    result_dict = deepcopy(parser_dict)

    # Handle includes if present
    if "include" in result_dict:
        include_dict = result_dict.pop("include")  # Remove include after processing

        # Initialize groups key if it doesn't exist
        if "groups" not in result_dict:
            result_dict["groups"] = []

        # Process each include directive
        for key, value in include_dict.items():
            if isinstance(value, dict):  # Ensure value is a dictionary
                if key in result_dict:
                    if isinstance(result_dict[key], dict):
                        result_dict[key].update(value)
                    elif isinstance(result_dict[key], list):
                        # Handle list type values
                        result_dict[key].extend(value if isinstance(value, list) else [value])
                else:
                    result_dict[key] = value

                # Mark included options as shared
                if key == "groups":
                    for group in value:
                        if isinstance(group, dict) and "options" in group:
                            for option in group["options"].values():
                                if isinstance(option, dict):
                                    option["is_shared_group_option"] = True

    return result_dict

