def _convert_non_cli_args(self, parser_name: str, values_dict: dict):
    if not values_dict:
        return

    def cast_to_bool(value):
        if not value:
            return False
        return str(value).lower() in ['true', '1', 'yes']

    def cast_to_int(value):
        try:
            return int(str(value))
        except (ValueError, TypeError):
            return value

    def cast_to_float(value):
        try:
            return float(str(value))
        except (ValueError, TypeError):
            return value

    def cast_to_list(value):
        if not value:
            return []
        return [item.strip() for item in str(value).split(',') if item.strip()]

    def cast_to_dict(value):
        if not value:
            return {}
        pairs = [pair.strip() for pair in str(value).split(',') if pair.strip()]
        dictionary = {}
        for pair in pairs:
            try:
                if ':' not in pair:
                    continue
                key, val = pair.split(':', 1)
                dictionary[key.strip()] = val.strip()
            except ValueError:
                continue
        return dictionary

    for key, value in list(values_dict.items()):
        if value is None:
            continue

        str_value = str(value)

        # Handle boolean values
        if str_value.lower() in ['true', 'false']:
            values_dict[key] = cast_to_bool(str_value)
        # Handle integer values
        elif str_value.strip('-').isdigit():
            values_dict[key] = cast_to_int(str_value)
        # Handle float values - more precise check
        elif '.' in str_value and str_value.replace('.', '', 1).strip('-').isdigit():
            values_dict[key] = cast_to_float(str_value)
        # Handle list values
        elif ',' in str_value and ':' not in str_value:
            values_dict[key] = cast_to_list(str_value)
        # Handle dict values
        elif ':' in str_value and ',' in str_value:
            values_dict[key] = cast_to_dict(str_value)
        else:
            values_dict[key] = str_value

