import os
import yaml

def get_plugin_spec_flatten_dict(plugin_dir):
    """
    Creates a flat dict from the plugin spec

    :param plugin_dir: A path to the plugin's dir
    :return: A flatten dictionary contains the plugin's properties
    """
    def flatten_dict(d, parent_key='', sep='_'):
        items = []
        for k, v in d.items():
            new_key = f"{parent_key}{sep}{k}" if parent_key else k
            if isinstance(v, dict):
                items.extend(flatten_dict(v, new_key, sep=sep).items())
            else:
                items.append((new_key, v))
        return dict(items)

    plugin_spec_dict = {}
    for root, dirs, files in os.walk(plugin_dir):
        for file in files:
            if file.endswith(".yaml"):
                yaml_file = os.path.join(root, file)
                try:
                    with open(yaml_file, 'r') as f:
                        yaml_data = yaml.safe_load(f)
                        if yaml_data:
                            # If this is the main plugin spec file, it should contain basic info
                            if "name" in yaml_data:
                                flattened = flatten_dict(yaml_data)
                                plugin_spec_dict.update(flattened)
                            # For other YAML files, prefix their keys with filename
                            else:
                                base_name = os.path.splitext(file)[0]
                                flattened = flatten_dict(yaml_data)
                                prefixed = {f"{base_name}_{k}": v for k, v in flattened.items()}
                                plugin_spec_dict.update(prefixed)
                except (yaml.YAMLError, IOError) as e:
                    continue  # Skip invalid YAML files

    return plugin_spec_dict

