@staticmethod
def is_file_exist(file_name):
    """
    Check if file name exist.
    :param file_name: File name.
    :type file_name: str
    :return: Return true (exist), false (do not exist, or invalid file name)
    :rtype bool
    """

    # Check
    if file_name is None:
        logger.warning("is_file_exist : file_name is None")
        return False
    elif not isinstance(file_name, str):
        logger.warning("is_file_exist : file_name is not a string, className=%s", type(file_name).__name__)
        return False

    # Go
    try:
        return os.path.isfile(file_name)
    except (OSError, ValueError) as e:
        logger.warning("is_file_exist : exception occurred for file_name=%s, error=%s", file_name, str(e))
        return False

