def minimalBases(classes):
    """Reduce a list of base classes to its ordered minimum equivalent"""
    if not __python3:
        classes = [c for c in classes if c <= ClassType]
    candidates = []
    for m in classes:
        for n in classes:
            if issubclass(n, m) and m < n:
                break
        else:
            if m >= candidates:
                candidates.remove(m)
            candidates.append(m)
    return candidates

