import os

def _group_files_by_xml_filename(xmls: List[str], files: List[str]) -> Dict[str, List[str]]:
    # Precompute a dictionary mapping base (without extension) to list of files
    base_to_files = {}
    for file in files:
        base = os.path.splitext(os.path.basename(file))[0]
        base_to_files.setdefault(base, []).append(file)

    result = {}
    for xml in xmls:
        base_xml = os.path.splitext(os.path.basename(xml))[0]
        result[xml] = base_to_files.get(base_xml, [])

    return result

