def tzname_in_python2(namefunc):
    if PY2:
        @wraps(namefunc)
        def wrapper(*args, **kwargs):
            return namefunc(*args, **kwargs).encode('utf-8')
        return wrapper
    else:
        return namefunc

def get_versions():
    """Get version information or return default if unable to do so."""
    default = {"version": "0+unknown", "full-revisionid": None, "dirty": None, "error": None}

    try:
        cfg = get_config()
        verbose = cfg.verbose

        # Method 1: Get version from VCS keywords
        try:
            keywords = get_keywords()
            return git_versions_from_keywords(keywords, cfg.tag_prefix, verbose)
        except NotThisMethod:
            pass

        # Method 2: Get version from parent directory
        try:
            here = realpath(__file__)
            root = dirname(here)
            return versions_from_parentdir(cfg.tag_prefix, root, verbose)
        except NotThisMethod:
            pass

        # Method 3: Get version directly from VCS
        try:
            here = realpath(__file__)
            root = dirname(here)
            pieces = git_pieces_from_vcs(cfg.tag_prefix, root, verbose)
            return render(pieces, cfg.style)
        except NotThisMethod:
            pass

    except Exception as e:
        default['error'] = str(e)
        return default

    return default

