    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    if explicit_mc is not None:
        return explicit_mc
    if not bases:
        return ClassType
    bases = minimalBases(bases)
    if not bases:
        return ClassType
    first = bases[0]
    try:
        return first.__class__
    except AttributeError:
        return ClassType

