from typing import Tuple, Set

def find_tags(text: str, replacer: callable = None) -> Tuple[Set, str]:
    if replacer is None:
        replacer = lambda x: x

    tags = set()
    code_block = False
    replaced_text = ""
    current_word = ""

    for char in text:
        if char == '`':
            code_block = not code_block
            replaced_text += char
        elif code_block:
            replaced_text += char
        elif char.isalpha() or char.isdigit() or char == '_':
            current_word += char
        else:
            if current_word:
                tags.add(current_word)
                if replacer:
                    replaced_text += replacer(current_word)
                else:
                    replaced_text += current_word
                current_word = ""
            replaced_text += char

    if current_word:
        tags.add(current_word)
        if replacer:
            replaced_text += replacer(current_word)
        else:
            replaced_text += current_word

    return tags, replaced_text

